function inspectState(){return new Promise((e=>{chrome.storage.local.get(["booqsDictToken"],(function(o){let n=o.booqsDictToken;if(n){let o="https://www.booqs.net/ja/api/v1/extension/is_logged_in?booqs_dict_token="+n,t={method:"POST",body:JSON.stringify({booqs_dict_token:n})};fetch(o,t).then((e=>e.json())).then((o=>{setUserData(o.data),e("loggedIn")})).catch((o=>{console.log(o),resetUserData(),e("error")}))}else resetUserData(),e("blankToken")}))}))}async function initializePage(){"loggedIn"==await inspectState()?renderProfile():renderLoginForm()}function setUserData(e){chrome.storage.local.set({booqsDictUserName:e.name}),chrome.storage.local.set({booqsDictIconUrl:e.icon_url}),chrome.storage.local.set({booqsDictPublicUid:e.public_uid}),chrome.storage.local.set({booqsDictToken:e.token})}function resetUserData(){chrome.storage.local.set({booqsDictUserName:""}),chrome.storage.local.set({booqsDictIconUrl:""}),chrome.storage.local.set({booqsDictPublicUid:""}),chrome.storage.local.set({booqsDictToken:""})}function renderProfile(){let e="",o="",n="";chrome.storage.local.get(["booqsDictPublicUid","booqsDictIconUrl","booqsDictUserName"],(function(t){e=t.booqsDictPublicUid,o=t.booqsDictIconUrl,n=t.booqsDictUserName;let s=`\n<div class="content has-text-centered">\n  <a href="https://www.booqs.net/ja/users/${e}" target="_blank" rel="noopener">\n    <figure class="mt-5 image is-128x128 mx-auto">\n      <img\n        class="is-rounded"\n        src="${o}"\n      />\n    </figure>\n\n    <h1 class="mt-3 is-size-4 has-text-weight-bold">\n      ${n}\n    </h1>\n  </a>\n  <button class="button is-warning is-light mx-auto my-3" id="logout-btn">ログアウト</button>\n</div>`;document.querySelector("#user-page").innerHTML=s,addEventToProfile()}))}function addEventToProfile(){document.querySelector("#logout-btn").addEventListener("click",(()=>{chrome.storage.local.get(["booqsDictToken"],(function(e){let o=e.booqsDictToken,n="https://www.booqs.net/ja/api/v1/extension/log_out?booqs_dict_token="+o,t={method:"POST",body:JSON.stringify({booqs_dict_token:o})};fetch(n,t).then((e=>e.json())).then((e=>{resetUserData(),renderLoginForm(),addEventToLoginForm()})).catch((e=>{console.log(e)}))}))}),!1)}function renderLoginForm(){document.querySelector("#user-page").innerHTML='\n<div class="content">\n<h1 class="mb-5 mt-3 has-text-centered is-size-2 has-text-weight-bold">\n  ログインフォーム\n</h1>\n<p>\n  ログインすることで、拡張機能から直接復習を設定できるようになります。\n</p>\n<p>\n  まだBooQsにアカウントを登録されていない方は、<a href="">こちら</a\n  >よりご登録ください。\n</p>\n</div>\n\n<form class="fetchForm">\n<div id="feedback">\n</div>\n<div class="field">\n  <label class="label">Email</label>\n  <div class="control">\n    <input\n      class="input"\n      id="booqs-email"\n      type="email"\n      placeholder="Email input"\n      value="sakushahushou@gmail.com"\n    />\n  </div>\n</div>\n\n<div class="field">\n  <label class="label">Password</label>\n  <div class="control">\n    <input\n      class="input"\n      id="booqs-password"\n      type="password"\n      placeholder="Text input"\n      value="foobar"\n    />\n  </div>\n</div>\n<input\n  type="button"\n  value="送信"\n  class="button is-primary is-link is-block mt-3"\n  id="booqs-login-btn"\n  style="width: 100%"\n/>\n</form>',addEventToLoginForm()}function addEventToLoginForm(){document.querySelector("#booqs-login-btn").addEventListener("click",(()=>{console.log("clickBtn");let e=document.querySelector("#booqs-email").value,o=document.querySelector("#booqs-password").value,n=`https://www.booqs.net/ja/api/v1/extension/sign_in?email=${e}&password=${o}`,t={method:"POST",mode:"cors",credentials:"include",body:JSON.stringify({email:e,password:o})};fetch(n,t).then((e=>e.json())).then((e=>{if("200"==e.status)setUserData(e.data),renderProfile();else{let e='\n                    <div class="notification is-danger is-light my-3">\n                    メールアドレスとパスワードの組み合わせが正しくありません。\n                    </div>';document.querySelector("#feedback").innerHTML=e}})).catch((e=>{console.log(e)}))}),!1)}initializePage();